 {!-- 

Site Index (template) -  site/index
============

Requirements:
-------------

Create a 'home' page in the CP and enter the entry_id for it below (this_page_id)

--}

{preload_replace:this_page_channel="pages"}
{preload_replace:this_page_entry_id="1"}

{exp:channel:entries
	channel="{this_page_channel}"
	entry_id="{this_page_entry_id}"
	limit="1"
	{!-- global:param_disable_all --}
	{global:param_cache_param}
}
	{if no_results}{redirect="404"}{/if}
	{embed="_partials/.header" 
		entry_id="{entry_id}"
		template="index"
		template-group="site"
	}
	<section class="content layout-2">
		<header class="section-header">
			{!-- Crumb navigation --}
			{embed="_partials/_nav-breadcrumb"}
			<h1 class="section-title">{if page_alternate_title}{page_alternate_title}{if:else}{title}{/if}</h1>
		</header>
		<article class="entry page-entry unit-1" role="main">
			{!-- Crumb navigation --}
			{embed="_partials/_nav-breadcrumb"}
			<h1 class="entry-title">{if page_alternate_title}{page_alternate_title}{if:else}{title}{/if}</h1>
			<div class="entry-content">{page_content}</div>
{/exp:channel:entries}	
		</article>
		<aside class="unit-2" role="complementary">
			{!-- Testimonial Panel --}
			{embed="testimonials/_panel-testimonials"
				featured-only="yes"
				limit="3"
				panel-title="Testimonials panel"
			}

			{!-- Latest News Entries Panel --}
			{embed='news/_panel-latest-entries'
				featured-only="yes"
				limit="3"
				title="Custom News Entries Title"
			}


		</aside>
	</section>
{embed="_partials/.footer"}

 {!--

Archive Category (template) -  news/.archive-category
================================

* Outputs and index of blog posts of a specific category.

--}

{preload_replace:this_index_channel="news"}

{exp:nsm_categories:archive
	category="{segment_4_category_id}"
	channel="{this_index_channel}"
	enable="entries"
	status="not closed"
}
	{embed="_partials/.header"
		description = "{segment_3_category_description}"
		entry_id="{entry_id}"
		template="category-archive"
		template-group="news"
		title="{segment_3_category_name}"
	}
	<section class="content" role="main">

		<header class="section-header">
		    <p class="section-title">{category_name} : Category archive</p>
			{if category_description}
			<div class="section-summary">{category_description}</div>
			{/if}
		</header>

		{!-- if there are no results to display --}
		{if channel_entries_total_results == "0"}
		<p class='no-results'>No results for {category_name}.</p>
		{/if}

		{!-- if there are results to display --}
		{if channel_entries_total_results != "0"}
		<div class="entry-index news-entry-index category-index">
    		{category_channel_entries}

    		<article class="entry">

    			<header class="entry-header">
    				<h2 class="entry-title">
    					<a href="{if page_uri}{page_uri}{if:else}{comment_url_title_auto_path}{/if}">
    						{title}
    					</a>
    				</h2>
    			</header>

    			<div class="entry-content">
    				{!-- Use a Wigwam channel field to utilise the following content/excerpt conditional --}
    				{news_content:excerpt}
    				{if "{news_content:has_excerpt}"}
    				<p><a href="{comment_url_title_auto_path}">Read more&hellip;</a></p>
    				{if:else}
    				<p><a href="{comment_url_title_auto_path}#comments">Leave a comment</a></p>
    				{/if}
    			</div>

    			<footer class="entry-meta">
    				<div class="author entry-author">
    					<strong>Author:</strong>
    					{if url}
    					<a href="{url}" class="author-name author-url" title="Vist {author}s website">{author}</a>
    					{if:else}
    					<span class="author-name">{author}</span>
    					{/if}

    					<img 
    						src="{exp:gravatar email='{email}' default='{global:nsm_gravatar_default_avatar}' size='48'}" 
    						alt="{screen_name} avatar" 
    						class="photo avatar gravatar" 
    						width="48"  />
    					{!-- 
    					{if avatar}<img class="photo avatar" src="{avatar_url}" alt="{author}s Avatar" height="{avatar_height}" width="{avatar_width}" />{/if}
    					{if photo}<img class="photo large" src="{photo_url}" alt="{author}s Photo" height="{photo_height}" width="{photo_width}" />{/if}
    					--}
    				</div>
    				<div class="entry-comments">
    					<strong>Comments:</strong>
    					<a 
    						href="{if page_uri}{page_uri}{if:else}{comment_url_title_auto_path}{/if}#comments" 
    						title="View comments">
    						{comment_total} comment{if "{comment_total}" != "1" }s{/if}
    					</a>
    				</div>
    				<div class="entry-published">
    					<strong>Published:</strong>
    					<a 
    						href="{path=news/archive}#d-{entry_date format="%Y-%m"}" 
    						title="View monthly archive for: {entry_date format="%M %Y"}"
    						class="time"
    					>
    						{entry_date format="%D, %F %d, %Y - %g:%i:%s"}
    						({relative_date})
    					</a>
    				</div>
    				{if edit_date}
    				<div class="entry-updated">
    					<strong>Last Modified:</strong>
    					<span class="time">
    						{edit_date format="%D, %F %d, %Y - %g:%i:%s"} ({relative_date})
    					</span>
    				</div>
    				{/if}
    				{if expiration_date}
    				<div class="entry-expires">
    					<strong>Expires:</strong>
    					<span class="time">
    						{expiration_date format="%D, %F %d, %Y - %g:%i:%s"} ({relative_date})
    					</span>
    				</div>
    				{/if}
    				{if "{categories}{category_id}{/categories}"}
    				<div class="categories">
    					<strong>Categories:</strong>
    					<ul>
    						{categories}
    						<li>
    							<a href="{path=news/archive}" title="Browse {category_name}">
    								{category_name}
    							</a>
    						</li>
    						{/categories}
    					</ul>
    				</div>
    				{/if}
    			</footer>
    		</article>

    		{/category_channel_entries}
		</div>
		{/if}
	</section>
	{embed="_partials/.footer"}
{/exp:nsm_categories:archive}

